package array;

public class LengthDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String[] classGroup = new String[4];
		classGroup[0] = "한인우";
		System.out.println(classGroup.length);
		classGroup[1] = "한인우1";
		System.out.println(classGroup.length);
		classGroup[2] = "한인우2";
		System.out.println(classGroup.length);
		classGroup[3] = "한인우3";
		System.out.println(classGroup.length);
	}

}
